import React, { useState } from 'react'

const useAuth = (initialValue) => {
    const [isAuth, setIsAuth] = useState(initialValue)


    const logIn = () => {
        setIsAuth(true)
    }
    const logOut = () => {
        setIsAuth(false)
    }


    return [isAuth, logIn, logOut]
}

export default useAuth
