import React, { useState } from 'react';

const WriteNotes = () => {
    const { text, setText } = useState({
        title: '',
        content: ''
    });


    const InputTextNotes = (e) => {
        const { name, value } = e.target;

        setText((oldData) => {
            return {
                ...oldData,
                [name]: value
            };

        });
    }
    return (
        <>
            <h5>Create Notes</h5>

            <label>Title</label>
            <input type='text' name='title' value={text.title} onChange={InputTextNotes} />
            <label>Content</label>
            <input type='text' name='content' value={text.content} onChange={InputTextNotes} />

        </>
    )
}

export default WriteNotes;
