import React, { useReducer } from 'react'



const reducer = (state, action) => {
    if (action.type === "inputText") {
        const { name, value } = action.feild.target;

        return state = {
            'inputController': {
                ...state.inputController,
                [name]: value
            }
        }
    } else if (action.type === 'submitData') {
        return state = {
            'data': {
                ...state.inputController
            }
        }
    }
}
const Contact = () => {
    const initialState = {
        inputController: {
            name: '',
            email: '',
            subject: '',
            message: ''
        },
        data: {}

    }
    const [state, dispatch] = useReducer(reducer, initialState);


    const InputEventText = (e) => {
        dispatch({ type: 'inputText', feild: e })
    }

    const submitDetails = () => {
        dispatch({ type: 'submitData' })
    }



    return (
        <>
            <section className="contact container">
                <h2 className="text-center mb-5">Contact me</h2>
                <div className="contact-wrapper mt-5">
                    <div className="row">
                        <div className="col-lg-6">
                            <h4>Get in Touch</h4>
                            <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Dignissimos harum corporis fuga
                                corrupti. Doloribus quis soluta nesciunt veritatis vitae nobis?</p>
                            <div className="detail-wrapper d-flex align-items-center justify-content-between">
                                <span><i className="text-danger fas fa-user"></i></span>
                                <div className="details d-flex flex-column">
                                    <label>Name</label>
                                    <span>Naresh Singh</span>
                                </div>
                            </div>
                            <div className="detail-wrapper d-flex align-items-center justify-content-between">
                                <span><i className="text-danger fas fa-user"></i></span>
                                <div className="details d-flex flex-column">
                                    <label>Name</label>
                                    <span>Naresh Singh</span>
                                </div>
                            </div>
                            <div className="detail-wrapper d-flex align-items-center justify-content-between">
                                <span><i className="text-danger fas fa-user"></i></span>
                                <div className="details d-flex flex-column">
                                    <label>Name</label>
                                    <span>Naresh Singh</span>
                                </div>
                            </div>
                        </div>
                        <div className="col-lg-6">
                            <h4>Message</h4>
                            <form>
                                <div className="first-field d-flex justify-content-between mb-2 mt-2">
                                    <div className="">
                                        <input type="text" className="form-control" name='name' value={state.name} placeholder="Name" onChange={InputEventText} />
                                    </div>
                                    <div className="">
                                        <input type="email" className="form-control" name='email' value={state.email} placeholder="Email" onChange={InputEventText} />
                                    </div>
                                </div>
                                <div className="mb-2 mt-2">
                                    <input type="text" className="form-control" name='subject' value={state.subject} placeholder="Subject" onChange={InputEventText} />
                                </div>
                                <div className="mb-2 mt-2">
                                    <textarea className="form-control" rows="3" name='message' value={state.message} placeholder="Message..." onChange={InputEventText}></textarea>
                                </div>
                                <button className="btn btn-danger" onClick={submitDetails}>Send Message</button>
                            </form>
                        </div>
                    </div>
                </div>
            </section>
        </>
    )
}

export default Contact
