import React, { useReducer, useEffect } from 'react'



export const reducer1 = (stateTyped, action) => {
    switch (action.type) {
        case 'TYPE_TEXT':
            // const { name, value } = action.feild.target;
            // return {
            //     ...state, entered: { ...state.entered, [name]: value }
            // }
            const { name, value } = action.feild.target;
            return {
                ...stateTyped, [name]: value
            }

        // case 'SUBMIT_TUPE':
        // action.form.preventDefault();
        // const userName = state.entered.name;
        // localStorage.setItem("users", JSON.stringify({ ...state.submitted, [userName]: state.entered }))

        // return {
        //     ...state, submitted: { ...state.submitted, [userName]: state.entered }
        // }


        default: return stateTyped;


    }
}

