import React, { useState } from 'react';

const WriteNotes = () => {
    const [text, inputText] = useState({
        title: '',
        content: '',
    });

    const InputEvent = (e) => {
        const { name, value } = e.target;
        inputText((oldData) => {
            return {
                ...oldData,
                [name]: value,
            };
        });
    };
    return (
        <>
            <h5>Create Notes</h5>

            <label>Title</label>
            <input type='text' name='title' placeholder='enter description...' onChange={InputEvent} />
            <label>Content</label>
            <input type='text' name='content' placeholder='enter description...' onChange={InputEvent} />

        </>
    )
}

export default WriteNotes;
