import React, { useReducer, useEffect, createContext } from 'react'

const UserData = createContext();

const reducer = (state, action) => {
    switch (action.type) {
        case 'TYPE_TEXT':
            const { name, value } = action.feild.target;
            return {
                ...state, entered: { ...state.entered, [name]: value }

            }
        case 'SUBMIT_TUPE':
            action.form.preventDefault();
            const userName = state.entered.name;

            return {
                ...state, submitted: { ...state.submitted, [userName]: state.entered }
            }

        default: return state;


    }
}

const Contact = () => {
    const initialState = {

        entered: {
            name: '',
            email: '',
            subject: '',
            message: ''
        },
        submitted: {}

    }

    const [state, dispatch] = useReducer(reducer, initialState);

    // const [textInput, setText] = useState({
    //     name: "",
    //     email: "",
    //     subject: "",
    //     message: ""
    // });
    // const [data, setData] = useState({});



    // const submitData = (e) => {
    //     e.preventDefault();
    //     const userName = textInput.name;


    //     setData((oldData) => {
    //         return {
    //             ...oldData,
    //             [userName]: textInput
    //         }
    //     });
    //     setText(
    //         {
    //             name: "",
    //             email: "",
    //             subject: "",
    //             message: ""
    //         }
    //     );

    // }



    // const InputEventText = (e) => {
    //     const { name, value } = e.target;

    //     setText((oldData) => {
    //         return {
    //             ...oldData,
    //             [name]: value
    //         }
    //     })

    // }

    useEffect(() => {
        localStorage.setItem("users", JSON.stringify(state.submitted))
    }, [state])


    return (
        <>
            <UserData.Provider value={state.submitted}>
                <section className="contact text-white">
                    <div className='container'>
                        <h2 className="text-center mb-5">Contact me</h2>
                        <div className="contact-wrapper mt-5">
                            <div className="row">
                                <div className="col-lg-6">
                                    <h4>Get in Touch</h4>
                                    <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Dignissimos harum corporis fuga
                                        corrupti. Doloribus quis soluta nesciunt veritatis vitae nobis?</p>
                                    <div className="detail-wrapper d-flex align-items-center justify-content-between">
                                        <span><i className="text-danger fas fa-user"></i></span>
                                        <div className="details d-flex flex-column">
                                            <label>Name</label>
                                            <span>Naresh Singh</span>
                                        </div>
                                    </div>
                                    <div className="detail-wrapper d-flex align-items-center justify-content-between">
                                        <span><i className="text-danger fas fa-user"></i></span>
                                        <div className="details d-flex flex-column">
                                            <label>Name</label>
                                            <span>Naresh Singh</span>
                                        </div>
                                    </div>
                                    <div className="detail-wrapper d-flex align-items-center justify-content-between">
                                        <span><i className="text-danger fas fa-user"></i></span>
                                        <div className="details d-flex flex-column">
                                            <label>Name</label>
                                            <span>Naresh Singh</span>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-lg-6">
                                    <h4>Message</h4>
                                    <form>

                                        <div className="first-field d-flex justify-content-between mb-2 mt-2">
                                            <div className="">
                                                <input type="text" className="form-control" name='name' value={state.name} placeholder="Name" onChange={(e) => dispatch({ type: 'TYPE_TEXT', feild: e })} />
                                            </div>
                                            <div className="">
                                                <input type="email" className="form-control" name='email' value={state.email} placeholder="Email" onChange={(e) => dispatch({ type: 'TYPE_TEXT', feild: e })} />
                                            </div>
                                        </div>
                                        <div className="mb-2 mt-2">
                                            <input type="text" className="form-control" name='subject' value={state.subject} placeholder="Subject" onChange={(e) => dispatch({ type: 'TYPE_TEXT', feild: e })} />
                                        </div>
                                        <div className="mb-2 mt-2">
                                            <textarea className="form-control" rows="3" name='message' value={state.message} placeholder="Message..." onChange={(e) => dispatch({ type: 'TYPE_TEXT', feild: e })}></textarea>
                                        </div>
                                        <button className="btn btn-danger" onClick={(e) => dispatch({ type: 'SUBMIT_TUPE', form: e })}>Send Message</button>
                                    </form>
                                    {/* <div className="details">
                                    <h5></h5>
                                </div> */}
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </UserData.Provider>
        </>
    )
}

export default Contact;
export { UserData }
