import React, { useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom'
import ColorsTheme from './components/ColorsTheme'

const Header = () => {
    // const [themeColor, setThemeColor] = useState({
    //     color: "#dc3545"
    // })

    const applyColorTheme = (e) => {

        // setThemeColor({
        //     color: e.target.style.backgroundColor
        // })

        localStorage.setItem('theme-color', e.target.style.backgroundColor);

        document.querySelector(':root').style.setProperty('--bs-danger', e.target.style.backgroundColor);

    }

    useEffect(() => {

        const colorSetted = localStorage.getItem('theme-color');
        if (colorSetted != themeColor.color) {
            localStorage.setItem('theme-color', themeColor.color);
        }
        document.querySelector(':root').style.setProperty('--bs-danger', colorSetted);


    })
    return (
        <>
            <nav className="navbar navbar-expand-sm bg-danger navbar-dark">
                <div className="container-fluid justify-content-between">
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <NavLink className="nav-link" to='/'>Home</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/restuarant">Restuarant</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/number">Number</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/number-with-reducer">Number with Reducer</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink className="nav-link" to="/todo-list">Todo List</NavLink>
                        </li>
                    </ul>
                    <ColorsTheme changeColor={applyColorTheme} />
                </div>
            </nav>
        </>
    )
}

export default Header
