import React, { useState } from 'react'
import MenuCards from './MenuCards';
import MenuItems from './MenuItems';

const App = () => {
  const [list, setList] = useState(MenuItems);

  const showItems = (e) => {
    const btnVal = e.target.innerHTML;
    console.log(btnVal);

    const filteredList = MenuItems.filter((val) => {
      return val.category === btnVal;
    });
    setList(filteredList);
  }

  return (
    <>
      <div className='container'>
        <div className="row">
          <div className='col-md-12'>
            <div className='navbar-items bg-dark p-2 d-flex justify-content-center'>
              <button className='btn btn-outline-light mx-2' onClick={showItems}>
                breakfast
              </button>
              <button className='btn btn-outline-light mx-2' onClick={showItems}>
                lunch
              </button>
              <button className='btn btn-outline-light mx-2' onClick={showItems}>
                dinner
              </button>
              <button className='btn btn-outline-light mx-2' onClick={() => { setList(MenuList); }}>
                All
              </button>
            </div>
          </div>
          <div className='col-md-12 mt-4'>
            <div className='row'>
              {
                list.map((val, ind) => {
                  return <MenuCards key={ind} desc={val.description} img={val.img} cat={val.category} price={val.price} item={val.item} />
                })
              }

            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default App
