import { createContext, useState } from 'react';
import './App.css';
import NoteDetails from './NoteDetails';
import WriteNotes from './WriteNotes';


const Notedata = createContext();

function App() {
  const [notes, setData] = useState([]);


  const setNoteData = (data) => {

    setData((oldData) => {
      return [...oldData, data]
    });
  }

  return (
    <>
      <Notedata.Provider value={notes}>
        <WriteNotes addNote={setNoteData} />
        <NoteDetails />
        {/* {
          notes.map((val, index) => {
            return (
              <NoteDetails key={index} id={index} titleValue={val.title} contentValue={val.content} />
            );
          })
        } */}
      </Notedata.Provider>
    </>
  );
}

export default App;
