import React, { useState } from 'react';

const CreateNotes = () => {
    const [text, inputText] = useState({
        title: '',
        content: '',
    });

    const InputEvent = (e) => {
        const { name, value } = e.target;
        inputText((oldData) => {
            return {
                ...oldData,
                [name]: value,
            };
        });
    };

    const addNote = () => {
        // props.addNotes(text);
        // inputText({
        //     title: '',
        //     content: '',
        // });
    };
    return (
        <>
            <div className="write">
                <h1>Create Notes</h1>
                <label>Title:</label>
                <input
                    type="text"
                    placeholder="Enter title..."
                    name="title"
                    value={text.title}
                    onChange={InputEvent}
                />
                <br />
                <br />
                <label>Content:</label>
                <input
                    type="text"
                    placeholder="Enter content..."
                    name="content"
                    value={text.content}
                    onChange={InputEvent}
                />
                <br />
                <br />
                <button onClick={addNote} type="button">
                    {' '}
                    Add{' '}
                </button>
            </div>
        </>
    );
};

export default CreateNotes;
