import React, { useEffect, useState } from 'react'
import Restuarant from './Restuarants/Restuarant';
import { Navigate, Route, Routes } from 'react-router-dom';
import Weather from './Weather/Weather';
import NumberRed from './NumberReducer/NumberRed';
import TodoList from './TodoList/TodoList';
import Home from './Home';
import Users from './components/Users';
import SignUp from './Accounts/SignUp';
import SignIn from './Accounts/SignIn';
import ProtectedRoutes from './ProtectedRoutes'
// import useAuth from './customHooks/useAuth'


const App = () => {
  const initialValCheck = () => {
    const initialVal = localStorage.getItem('isAuth');

    if (!initialVal)
      return false
    else return initialVal
  }

  const [auth, setAuth] = useState(initialValCheck());


  const logIn = () => {

    setAuth(true)

    setTimeout(() => {
      window.location.href = '/'
    }, 1000)
  }
  const logOut = () => {
    setAuth(false)
    setTimeout(() => {
      window.location.href = '/sign-in'
    }, 1000)
  }


  useEffect(() => {
    localStorage.setItem('isAuth', auth)
  }, [auth])



  return (
    <>

      <div className='content'>

        <Routes>
          <Route path='/' element={<ProtectedRoutes />} >
            <Route path='/' exact element={<Home />} />
            <Route path='/restuarant' element={<Restuarant />} />
            <Route path='/weather' element={<Weather />} />
            <Route path='/number-with-reducer' element={<NumberRed />} />
            <Route path='/todo-list' element={<TodoList />} />
            <Route path='/users-data' element={<Users />} />
            <Route path='/sign-up' element={<SignUp />} />
          </Route>

          <Route path='/sign-in' element={<SignIn />} />


        </Routes>
        <button onClick={logIn} className='btn'>Log in</button>
        <button onClick={logOut} className='btn'>Log out</button>
      </div>
    </>
  )
}

export default App
