import React, { useState } from 'react'
import MenuCards from './MenuCards';
import MenuItems from './MenuItems';

const reverseList = ['All', ...new Set(MenuItems.map((val) => {
    return val.category;
}))];

// const reverseList = categoryList.reverse();

const Restuarant = () => {
    const totalItems = MenuItems.length;
    const [list, setList] = useState(MenuItems);
    const [total, setTotal] = useState(totalItems);
    const [activeClass, setActive] = useState('All');


    const showItems = (e) => {
        const btnVal = e.target.innerHTML;
        // const AllBtn = document.querySelectorAll('.navbar-items button');
        // AllBtn.forEach((val) => {
        //   val.classList.remove('active');
        // });
        // e.target.classList.add('active');

        setActive(btnVal);


        // console.log(activeClass);

        if (btnVal === 'All') {
            setList(MenuItems);
            setTotal(MenuItems.length);
        } else {

            const filteredList = MenuItems.filter((val) => {
                return val.category === btnVal;
            });
            setTotal(filteredList.length);
            setList(filteredList);
        }


    }

    return (
        <>
            <div className='container'>
                <div className="row">
                    <div className='col-md-12'>
                        <div className='navbar-items p-2 d-flex justify-content-center'>
                            {
                                reverseList.map((val, ind) => {
                                    return (
                                        <>
                                            <button key={ind} id={`btn-${ind + 1}`} className={activeClass === val ? 'btn btn-outline-success mx-2 active' : 'btn btn-outline-success mx-2'} onClick={showItems}>
                                                {val}
                                            </button>
                                        </>
                                    )
                                })
                            }

                        </div>
                    </div>
                    <div className='col-md-12 mt-4'>
                        <div className='row'>
                            <div className='results col-12 my-4'>
                                showing {total} of {totalItems}
                            </div>
                            {
                                list.map((val, ind) => {
                                    return <MenuCards key={ind} desc={val.description} img={val.img} cat={val.category} price={val.price} item={val.item} />
                                })
                            }

                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Restuarant;
