import React, { useState } from 'react';

const WriteNotes = () => {
    const [text, setText] = useState({
        title: '',
        content: '',
    });


    const InputTextNotes = (e) => {
        const { name, value } = e.target;

        setText((oldData) => {
            return {
                ...oldData,
                [name]: value,
            };
        });
    };
    return (
        <>
            <h5>Create Notes</h5>

            <label>Title</label>
            <input type='text' name='title' placeholder='enter description...' value={text.title} onChange={InputTextNotes} />
            <label>Content</label>
            <input type='text' name='content' placeholder='enter description...' value={text.content} onChange={InputTextNotes} />

            <button type='button' onClick={addNotes}>Add Notes</button>

        </>
    )
}

export default WriteNotes;
