import React, { useEffect, useState } from 'react'
import './Account.css';

const SignIn = () => {
    const [state, setstate] = useState({ inputUser: '', inputPassword: '' })
    const [userData, updateUserData] = useState({})
    const [curUser, checkUserData] = useState('guest')
    const [logged, signIn] = useState('')


    const InputUserDetails = (e) => {
        const { id, value } = e.target;


        setstate((oldData) => {
            return {
                ...oldData, [id]: value
            }
        })


    }


    const authenticateUser = (e) => {

        e.preventDefault();
        const userAuth = Object.entries(userData).filter((val, ind) => {

            if (state.inputUser === val[0] && state.inputPassword === val[1].inputPassword) {
                // console.log('user Authentic')
                const userName = state.inputUser;
                return userName
                alert("executed")

            }


        })
        console.log(userAuth)
        if (userAuth) {

            checkUserData(userAuth);
            signIn(userAuth);
        } else {
            alert('Enter Credential')
        }
    }

    useEffect(() => {

        const users = localStorage.getItem('users');
        if (users) {
            updateUserData(JSON.parse(users));
        }


    }, [])
    return (
        <>



            <div className="container">
                <div className="card card-container">
                    {curUser != '' ? '' : <p className='alert alert-danger text-center'><strong>User Credential Incorrect</strong></p>}
                    {logged ? <p className='alert alert-success text-center'><strong>Welcome! {logged}</strong></p> : ''}
                    <img id="profile-img" className="profile-img-card" src="//ssl.gstatic.com/accounts/ui/avatar_2x.png" />
                    <p id="profile-name" className="profile-name-card">Sign In</p>
                    <form className="form-signin" onSubmit={authenticateUser}>
                        <span id="reauth-email" className="reauth-email"></span>
                        <input type="text" id="inputUser" value={state.inputUser} onChange={InputUserDetails} className="form-control" placeholder="User name" />
                        <input type="password" id="inputPassword" value={state.inputPassword} onChange={InputUserDetails} className="form-control" placeholder="Password" />
                        <div id="remember" className="checkbox">
                            <label>
                                <input type="checkbox" value="remember-me" /> Remember me
                            </label>
                        </div>
                        <button className="btn btn-lg btn-outline-danger btn-block btn-signin mt-3" type="submit">Sign in</button>
                    </form>
                    <a href="#" className="forgot-password">
                        Forgot the password?
                    </a>
                </div>
            </div>
        </>
    )
}

export default SignIn
