import { useState } from 'react';
import './App.css';
import NoteDetails from './NoteDetails';
import WriteNotes from './WriteNotes';

function App() {
  const [notes, setData] = useState([]);


  const setNoteData = (data) => {
    setData((oldData) => {
      return [...oldData, data]
    });
  }

  return (
    <>
      <WriteNotes addNote={setNoteData} />

      {
        notes.map((val, index) => {
          return
          {
            <NoteDetails key={index} id={index} titleValue={val.title} contentValue={val.content} />
          }
        })
      }

    </>
  );
}

export default App;
