import React, { useState } from 'react'
import Menucards from './Menuacards';
import MenuList from '../Restuarants/MenuItems';

const Restuarantsnew = () => {
    const [list, setList] = useState(MenuList);
    const [hasActive, setActiveClass] = useState('All');
    const categoryList = ["All", ...new Set(MenuList.map((val) => {
        return val.category;
    }))];

    const setBtnList = (e) => {
        console.log(e.target.innerHTML);
        const btnVal = e.target.innerHTML;

        if (btnVal == 'All') {
            setList(MenuList);
        }
        else {
            const filteredList = MenuList.filter((val) => {
                return val.category === btnVal;
            });
            setList(filteredList);
        }
    }


    return (
        <>
            <div className='container'>
                <div className="row">
                    <div className='col-md-12'>
                        <div className='navbar-items p-2 d-flex justify-content-center'>
                            {
                                categoryList.map((val, ind) => {
                                    return (
                                        <>
                                            <button className={`btn btn-outline-success mx-2 ${hasActive === val ? 'active' : ''}`} onClick={setBtnList}>{val}</button>
                                        </>
                                    )
                                })
                            }

                        </div>
                    </div>
                    <div className='col-md-12 mt-4'>
                        <div className='row'>
                            {
                                list.map((val, ind) => {
                                    return (
                                        <Menucards key={ind} id={ind} desc={val.description} img={val.img} cat={val.category} price={val.price} item={val.item} />
                                    )
                                })
                            }
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}

export default Restuarantsnew
